import java.util.*;
public class Main {

	static Scanner sc=new Scanner(System.in);
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=sc.nextInt();
		
		for(int j=1; j<=i;j++) {
			int a=sc.nextInt();
			int b=sc.nextInt();
			System.out.println(a+b);
		}     
    }      
}