import java.util.*;
public class Main {

	static Scanner sc=new Scanner(System.in);
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
			
		int a=sc.nextInt();
		
		for(int i=1; i<10;i++) {
			System.out.println(a+" * "+i+" = "+a*i);
		}     
    }      
}
